package HomeWork.homeWork2;import java.util.Scanner;/** * Created by Ангелин on 19.07.2015. * Дан массив натуральных чисел. Найти сумму элементов, кратных данному K. */public class ArraysTask1 {    public static void main(String[] args){        int []array = new int[10];        for (int i = 0; i < array.length; i++){            array[i] = (int)(Math.random()*100);        }        for (int i = 0; i < array.length; i++)            System.out.print(array[i]+" ");        System.out.println();        System.out.print("Введите число К:");        Scanner scan = new Scanner(System.in);        int k = scan.nextInt();        System.out.println(k);        int sum = 0;        for (int i = 0; i < array.length; i++){            if (array[i] % k == 0)                sum += array[i];        }        System.out.println("Сумма элементов кратных числу "+ k +" составляет - "+sum);    }}