/** * Created by Ангелин on 19.07.2015. * Дана последовательность целых чисел a1, a2, ..., an. Выяснить, какое число встречается раньше – положительное или отрицательное. */public class ArraysTask3 {    public static void main(String[] args) {        int[] array = {0, -52, 37, -63, 67, 21, -75};        for (int i = 0; i < array.length; i++){            if (array[i] > 0) {                System.out.println("Первым встречаеться положительное число.");                System.out.println(array[i]);                break;            }            if (array[i] < 0) {                System.out.println("Первым встречаеться отрицательное число");                System.out.println(array[i]);                break;            }        }    }}